mapper.update:
update orders


        status = #{status}


        cancel_reason = cancelReason


        cancel_time = cancelTime





where id = #{id}

mapper.select:
select * from orders


        and number like concat('%',#{number},'%')


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


        and phone like concat('%',#{phone},'%')


        and status = #{status}


        and user_id = #{userId}


        and marketer_id = #{marketerId}


order by order_time desc

select count(id) from orders


        and order_time > #{begin}


        and order_time < #{end}


        and status = #{status}


        and marketer_id = #{marketerId}

select sum(amount) from orders


        and order_time > #{begin}


        and order_time < #{end}


        and status = #{status}

select od.name, sum(od.number) number from order_detail od,orders o where od.order_id = o.id and o.status = 4
and o.order_time > #{begin}
and o.order_time < #{end}
group by od.name order by number desc limit 0,10

select count(id) from orders
where status = #{status}

    and marketer_id = #{id}
